(function(){
function v(){document.documentElement.style.setProperty("--vh",window.innerHeight*.01+"px")}
v();window.addEventListener("resize",v);

let l=!0,p=!1;

document.addEventListener("DOMContentLoaded",function(){
  c();r();b();
});

function c(){
  if(!l)return;
  const{x:e,y:t}=window;
  window.onscroll=()=>window.scrollTo(e,t);
  document.body.style.cssText=
  "position:fixed;height:calc(var(--vh,1vh)*100);overflow-y:scroll";
}

function u(){
  window.onscroll=null;
  const e=document.getElementById("sec");
  if(e){
    e.style.cssText="opacity:0;transition:1.5s";
    setTimeout(()=>e.style.display="none",1500)
  }
  document.body.style.cssText="";
  l=!1;
}

function r(){
  if(document.body.classList.contains("elementor-editor-active"))return;
  document.querySelectorAll(".af").forEach(e=>e.classList.add("active"));
}

function s(){
  const e=document.querySelector(".aw");
  if(e)e.style.display="block";
}

function a(){
  return document.querySelector(".idb-audio-el")||document.getElementById("song")
}
function m(){
  return document.querySelector(".idb-mute-sound")
}

function y(){
  window.__BISDEV_MUSIC_ALLOWED=!0;
  const e=m();
  if(e){e.click();return}
  const t=a();
  if(t)t.play().catch(()=>{})
}

function h(){
  const e=a();
  if(!e)return;
  p=!e.paused;
  e.pause();
}

function f(){
  if(!p)return;
  const e=a();
  if(!e)return;
  e.play().catch(()=>{
    const t=m();
    if(t){
      t.classList.add("idb-attention");
      setTimeout(()=>t.classList.remove("idb-attention"),2500)
    }
  })
}

function b(){
  const e=document.getElementById("open");
  if(!e)return;
  e.addEventListener("click",function(){
    u();s();y();
  },{once:!0})
}

document.addEventListener("visibilitychange",()=>{
  document.hidden?h():setTimeout(f,200)
});
window.addEventListener("blur",h);
window.addEventListener("focus",()=>setTimeout(f,200));
})();
