(function(){
console.log("BISDEV SCRIPT MASUK");
var locked=false;
function prevent(e){e.preventDefault();}
function setVh(){document.documentElement.style.setProperty("--vh",window.innerHeight*.01+"px")}

function lock(){
  document.documentElement.style.overflow="hidden";
  document.documentElement.style.height="100%";
  document.addEventListener("wheel",prevent,{passive:false});
  document.addEventListener("touchmove",prevent,{passive:false});
}


function unlock(){
  document.documentElement.classList.remove("lock-scroll");
  document.body.classList.remove("lock-scroll");
  document.removeEventListener("wheel",prevent);
  document.removeEventListener("touchmove",prevent);

  var sec=document.getElementById("sec");
  if(sec){
    sec.style.cssText="opacity:0;transition:opacity 1.5s ease-in-out";
    setTimeout(function(){sec.style.display="none"},1500);
  }

  window.__BISDEV_MUSIC_ALLOWED=true;
  var btn=document.querySelector(".idb-audio-box .idb-mute-sound");
  if(btn) try{btn.click()}catch(e){}
}

document.addEventListener("DOMContentLoaded",function(){
  setVh();
  lock(); // ðŸ”’ SEKARANG BENAR-BENAR JALAN
  var open=document.getElementById("open");
  if(open) open.addEventListener("click",unlock,{once:true});
});

window.addEventListener("resize",setVh);
})();
